import React, { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { AuthContext } from '../context/AuthContext';

const API = 'http://localhost:3001';

const Novosti = () => {
  const [novosti, setNovosti] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    fetch(`${API}/novosti`)
      .then(res => res.json())
      .then(data => setNovosti(Array.isArray(data) ? data : []))
      .catch(err => console.error('Greška pri dohvatanju novosti:', err))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div>
      <Navbar />

      {/* Info o korisniku (možeš ukloniti ako ne treba) */}
      <div style={{ color: 'red', fontWeight: 'bold', textAlign: 'center', marginTop: 10 }}>
        {user ? `Prijavljen kao: ${user.KorisnickoIme}` : 'Niste prijavljeni'}
      </div>

      <section className="novosti-section" style={{ padding: '24px 16px', maxWidth: 1200, margin: '0 auto' }}>
        <h1 className="novosti-title" style={{ textAlign: 'center', marginBottom: 24 }}>Novosti</h1>

        {loading ? (
          <p style={{ textAlign: 'center' }}>Učitavanje…</p>
        ) : novosti.length === 0 ? (
          <p style={{ textAlign: 'center' }}>Nema trenutno dostupnih novosti.</p>
        ) : (
          <div
            className="novosti-grid"
            style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))',
              gap: 16
            }}
          >
            {novosti.map((n) => (
              <Link
                key={n.ID}
                to={`/novosti/${n.ID}`}
                style={{ textDecoration: 'none', color: 'inherit' }}
              >
                <article
                  className="novost-kartica"
                  style={{
                    border: '1px solid #eee',
                    borderRadius: 12,
                    overflow: 'hidden',
                    background: '#fff',
                    boxShadow: '0 2px 10px rgba(0,0,0,0.06)',
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column'
                  }}
                >
                  {n.Putanja && (
                    <img
                      src={`${API}/uploads/novosti/${n.Putanja}`}
                      alt={n.Naslov}
                      className="novost-slika"
                      style={{ width: '100%', height: 160, objectFit: 'cover' }}
                    />
                  )}
                  <div style={{ padding: '14px 16px' }}>
                    <h3 style={{ margin: '0 0 8px', fontSize: 18 }}>{n.Naslov}</h3>
                    <p style={{ margin: 0, color: '#555', lineHeight: 1.4 }}>
                      {n.Tekst && n.Tekst.length > 120 ? n.Tekst.slice(0, 120) + '…' : n.Tekst}
                    </p>
                  </div>
                </article>
              </Link>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default Novosti;
