import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';

const API = 'http://localhost:3001';

export default function NovostDetalj() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [novost, setNovost] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const load = async () => {
      try {
        
        const res = await fetch(`${API}/novosti/${id}`);
        if (!res.ok) throw new Error('no /novosti/:id');
        const data = await res.json();
        setNovost(data);
      } catch {
        
        try {
          const listRes = await fetch(`${API}/novosti`);
          const list = await listRes.json();
          const found = Array.isArray(list) ? list.find(x => String(x.ID) === String(id)) : null;
          setNovost(found || null);
        } catch {
          setNovost(null);
        }
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [id]);

  return (
    <div>
      <Navbar />
      <section style={{ padding: '24px 16px', maxWidth: 900, margin: '0 auto' }}>
        <button
          onClick={() => navigate(-1)}
          style={{
            marginBottom: 16,
            border: '1px solid #ddd',
            padding: '8px 12px',
            borderRadius: 8,
            background: '#fff',
            cursor: 'pointer'
          }}
        >
          ← Nazad
        </button>

        {loading ? (
          <p>Učitavanje…</p>
        ) : !novost ? (
          <p>Novost nije pronađena.</p>
        ) : (
          <article>
            <h1 style={{ marginBottom: 12 }}>{novost.Naslov}</h1>

            {novost.Putanja && (
              <img
                src={`${API}/uploads/novosti/${novost.Putanja}`}
                alt={novost.Naslov}
                style={{
                  width: '100%',
                  maxHeight: 420,
                  objectFit: 'cover',
                  borderRadius: 12,
                  marginBottom: 16
                }}
              />
            )}

            <p style={{ whiteSpace: 'pre-wrap', lineHeight: 1.6 }}>{novost.Tekst}</p>

            <div style={{ marginTop: 24 }}>
              <Link to="/novosti" style={{ textDecoration: 'none' }}>
                ← Nazad na sve novosti
              </Link>
            </div>
          </article>
        )}
      </section>
    </div>
  );
}
