import React, { useEffect, useState, useContext } from 'react';
import Navbar from '../components/Navbar';
import { AuthContext } from '../context/AuthContext';

const Novosti = () => {
  const [novosti, setNovosti] = useState([]);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    fetch('http://localhost:3001/novosti')
      .then(res => res.json())
      .then(data => setNovosti(data))
      .catch(err => console.error('Greška pri dohvatanju novosti:', err));
  }, []);

  return (
    <div>
      <Navbar />

      {/* TEST prikaz korisnika */}
      <div style={{ color: 'red', fontWeight: 'bold', textAlign: 'center', marginTop: '10px' }}>
        {user ? `Prijavljen kao: ${user.KorisnickoIme}` : 'Niste prijavljeni'}
      </div>

      <section className="novosti-section">
        <h2 className="novosti-title">Novosti</h2>

        {novosti.length === 0 ? (
          <p>Nema trenutno dostupnih novosti.</p>
        ) : (
          novosti.map((n) => (
            <div key={n.ID} className="novost-kartica">
              {n.Putanja && (
                <img
                  src={`http://localhost:3001/uploads/novosti/${n.Putanja}`}
                  alt={n.Naslov}
                  className="novost-slika"
                />
              )}
              <h3>{n.Naslov}</h3>
              <p>{n.Tekst}</p>
            </div>
          ))
        )}
      </section>
    </div>
  );
};

export default Novosti;
